import React, { Component, useState } from "react";
// import { createProject } from '../../store/actions/projectActions';
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";
// import Select from './Select';
// import Select from "react-select";
import CustomSelect from "./Select";
// import { Category } from '@material-ui/icons';
import { createProductAction } from "..//../store/actions/productActions";
import firebase from "firebase";
import { storage } from "../../config/fbConfig";
import { event } from "jquery";
import Firebase from "firebase";
import config from "../../config/fbConfig";

const options = [
  { value: "smartphone", label: "СМАРТФОНЫ" },
  { value: "pads", label: "ПЛАНШЕТЫ" },
  { value: "notebooks", label: "НОУТБУКИ" },
  { value: "smartwatches", label: "СМАРТ-ЧАСЫ" },
  { value: "headphones", label: "НАУШНИКИ" },
  { value: "accessories", label: "АКСЕССУАРЫ" },
  { value: "technique", label: "ТЕХНИКА" },
];  



export class ModerateProductEntity extends React.Component {
  constructor(props) {
    super(props);


    this.state = {
      selectOptions: [],
      cartItems: [],
      title: "",
      Category: "",
      Region: "",
      Manufacturer: "",
      Brand: "",
      Fortress: "",
      Colour: "",
      Sugar: "",
      Grape: "",
      Serving_temperature: "",
      Vintage: "",
      Products_webpage: "",
      content: "",
      description: "",
      delivery: "",
      img: "",
      name: "",
      picture: "",
      uid: 0,
      sizes: [],
      defaultQty: 0,
      moderated:false,
      url: "",
      urlVideo: "",
      developers:[]
     };

}

componentDidMount() {
  this.getUserData();
}

componentDidUpdate(prevProps, prevState) {
  if (prevState !== this.state) {
    this.writeUserData();
  }
}

writeUserData = () => {
  Firebase.database()
    .ref("/")
    .set(this.state);
  console.log("DATA SAVED");
};
developer

getUserData = () => {
  let ref = Firebase.database().ref("/");
  ref.on("value", snapshot => {
    const state = snapshot.val();
    this.setState(state);
  });
};

handleSubmit = event => {
  console.log('e is -----',e)
  event.preventDefault();
  let name = this.name.value;
  let title = this.title.value;
  let uid = this.uid.value;

  if (uid && name && title) {
    const { developers } = this.state;
    const devIndex = developers.findIndex(data => {
      return data.uid === uid;
    });
    developers[devIndex].name = name;
    developers[devIndex].title = title;
    this.setState({ developers });
  } else if (name && title) {
    const uid = new Date().getTime().toString();
    const { developers } = this.state;
    developers.push({ uid, name, title });
    this.setState({ developers });
  }

  this.name.value = "";
  this.title.value = "";
  this.uid.value = "";
};

removeData = developer => {
  const { developers } = this.state;
  const newState = developers.filter(data => {
    return data.uid !== developer.uid;
  });
  this.setState({ developers: newState });
};

updateData = developer => {
  this.uid.value = developer.uid;
  this.name.value = developer.name;
  this.title.value = developer.title;
};

render() {
  const { developers } = this.state;
  console.log('devs ',developers)
  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-xl-12">
            <h1>Firebase Development Team</h1>
          </div>
        </div>
        <div className="row">
          <div className="col-xl-12">
            {developers.map(developer => (
              <div
                key={developer.uid}
                className="card float-left"
                style={{ width: "18rem", marginRight: "1rem" }}
              >
                <div className="card-body">
                  <h5 className="card-title">{developer.name}</h5>
                  <p className="card-text">{developer.title}</p>
                  <button
                    onClick={() => this.removeData(developer)}
                    className="btn btn-link"
                  >
                    Delete
                  </button>
                  <button
                    onClick={() => this.updateData(developer)}
                    className="btn btn-link"
                  >
                    Edit
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
        <div className="row">
          <div className="col-xl-12">
            <h1>Add new team member here</h1>
            <form onSubmit={this.handleSubmit}>
              <div className="form-row">
                <input type="hidden" ref="uid" />
                <div className="form-group col-md-6">
                  <label>Name</label>
                  <input
                    type="text"
                    ref="name"
                    className="form-control"
                    placeholder="Name"
                  />
                </div>
                <div className="form-group col-md-6">
                  <label>Role</label>
                  <input
                    type="text"
                    ref="role"
                    className="form-control"
                    placeholder="Role"
                  />
                </div>
              </div>
              <button type="submit" className="btn btn-primary">
                Save
              </button>
            </form>
          </div>
        </div>
        <div className="row">
          <div className="col-xl-12">
            <h3>
              Tutorial{" "}
              <a href="https://sebhastian.com/react-firebase-real-time-database-guide">
                here
              </a>
            </h3>
          </div>
        </div>
      </div>
    </>
  );


   //------------------------------------------------------------------------

}}
const mapStateToProps = (state) => {
  return {
    auth: state.firebase.auth,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    createProductAction: (product) => dispatch(createProductAction(product)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ModerateProductEntity);















