  //-----------------------------------------------------------------------uploadFileToserverFunctions
  UplpadComponent = () => {
    const [fileData, setFileData] = useState();

    const fileChangeHandler = (e) => {
      setFileData(e.target.files[0]);
    };

    const onSubmitHandler = (e) => {
      e.preventDefault();
      console.log("console log e in UplpadComponent -  ", e);
      // Handle File Data from the state Before Sending
      const data = new FormData();

      data.append("image", fileData);

      this.state.url = "./images/" + "Lesson" + "--" + fileData.name;  //changing url
      fetch("http://localhost:5000/multiple", {
        method: "POST",
        body: data,
      })
        .then((result) => {
          console.log("File Sent Successful");
          console.log(
            "filedata is ------ image ",
            "Lesson" + "--" + fileData.name
          );
        })
        .catch((err) => {
          console.log(err.message);
        });
    };

    return (
      <>
        <form onSubmit={onSubmitHandler} onChange='submit'>
          <input type="file" onChange={fileChangeHandler} />
          <br />
          <br />

          {/* <button type="submit">Submit File to Backend</button> */}
        </form>
      </>
    );
  };



  UplpadComponentVideo = () => {
    const [fileData, setFileData] = useState();

    const fileChangeHandler = (e) => {
      setFileData(e.target.files[0]);
    };

    const onSubmitHandler = (e) => {
      e.preventDefault();
      console.log("console log e in UplpadComponent -  ", e);
      // Handle File Data from the state Before Sending
      const data = new FormData();

      data.append("image", fileData);
      this.state.urlVideo = "./images/" + "Lesson" + "--" + fileData.name;
      fetch("http://localhost:5000/single", {
